import React, {Component} from 'react';
import './App.css';
import LeftNavigator from './LeftNavigator/LeftNavigator';
import LandingImage from './LandingImage/LandingImage';
// import Portfolio from './Portfolio/Portfolio';
// import About from './About/About.js';
// import Fashion from './Fashion/Fashion.js';
// import Slider from './Components/photoSlider/Slider.js';

class App extends Component {
  state = {
    currentPage : "Home",
    LeftNav: true
  }
  contentArea = (<LandingImage />)  
  //contentSwitching
  switchContentAreaHandle = (page) => {
    if(page === "Portfolio"){
      //this.contentArea = (<Portfolio click={this.dialogHandler} app={this}/>);
      this.setState({
        currentPage: "Portfolio"
      });
    }
    else if(page === "Work")
      console.log(page);
    else if(page === "About"){
      console.log(page);
      //this.contentArea = (<About/>);
      this.setState({
        currentPage: "About"
      });
    }
    else if (page === "Contact")
      console.log(page);
    else if(page === "- Fashion & People"){
      //this.contentArea = (<Fashion click={this.dialogHandler} app={this}/>);
      this.setState({
        currentPage: "- Fashion & People"
      }); 
    }
    else{
      this.contentArea = (<LandingImage />);
      this.setState({
        currentPage: "Home"
      });
    }
  }

  //Menu button Handler
  leftNavHandler = () => {
    var leftNav = !this.state.LeftNav;

    this.setState({
      LeftNav: leftNav
    });
  }

  leftNavVar=true;
  constructor(props){
    super(props);
    if(window.innerWidth<800){
      this.leftNavVar=false;
    }
  }

  render(){
    if(this.leftNavVar===false){
      var LeftNavigation =(<LeftNavigator style={false} click={this.switchContentAreaHandle}/>);
      this.leftNavVar=true;
      this.setState({LeftNav: false});
    }
    else
      var LeftNavigation =(<LeftNavigator style={this.state.LeftNav} click={this.switchContentAreaHandle}/>);

    return (
      <div className="App">
        {LeftNavigation}     
        {this.contentArea}   
      </div>
    );
  }
}

export default App;
