import React, {Component} from 'react';
import './Slider.css';

class Slider extends Component {
    state={
        sliderIndex:0
    };
    images = null;

    slideClickHandler(clickDirection){
        var index = this.state.sliderIndex;
        index++;
        var style = {height: "100%"};
        var imgStyle = {width: '100%'};
        
        if(index === this.props.series.length)
            index = 0;

        this.images = (<div style={style}><img src={this.props.series[index]}/></div>);

        if(Array.isArray(this.props.series[index])){
            var imageList = [];
            this.props.series[index].map(function(v, k){
                imageList.push((<img src={v} key={k}/>));
            });
            this.images = (<div style={style}>{imageList}</div>);
        }

        this.setState({
            sliderIndex: index
        });

    }

    render(){
        var imageList = this.images;
        var style = {height: "100%"};
        if(imageList === null){
            if(Array.isArray(this.props.series[this.state.sliderIndex])){
                imageList = [];
                this.props.series[this.state.sliderIndex].map(function(v, k){
                    imageList.push((<img src={v} key={k}/>));
                });
                imageList = (<div style={style}>{imageList}</div>);
            }
            else{                
                imageList = (<div style={style}><img src={this.props.series[this.state.sliderIndex]}/></div>)
            }
        }
        return(
            <div className='Slider'>
                <div className='photos'>
                    <button className='leftButton' onClick={this.slideClickHandler.bind(this,"left")}>&#8249;</button>
                    {imageList}
                    <button className='closeButton' onClick={this.props.closeClick}>X</button>
                    <button className='rightButton' onClick={this.slideClickHandler.bind(this,"right")}>&#8250;</button>
                </div>
            </div>        
        );
    }
};

export default Slider;
