import React, {Component} from 'react';
import './App.css';
import LeftNavigator from './LeftNavigator/LeftNavigator';
import LandingImage from './LandingImage/LandingImage';
import Portfolio from './Portfolio/Portfolio';
import About from './About/About.js';
import Fashion from './Fashion/Fashion.js';
import Slider from './Components/photoSlider/Slider.js';
import Title from './Title/Title.js';

class App extends Component {
  state = {
    currentPage : "Home",
    LeftNav: true
  };

  //For image pop up dialog
  dialogVariable = null;
  dialogHandler(obj){
    console.log("Dialog opened!");
    this.dialogVariable = <Slider 
                            closeClick={this.dialogCloseHandler.bind(this,"")} 
                            series={obj.series}
                          />;
    this.setState({
      currentPage: obj.page
    });
  } 
  dialogCloseHandler(){
    this.dialogVariable = null;
    this.setState({
      currentPage: ""
    });
  }

  contentArea = (<LandingImage />)  
  switchContentAreaHandle = (page) => {
    if(page === "Portfolio"){
      this.contentArea = (<Portfolio click={this.dialogHandler} app={this}/>);
      this.setState({
        currentPage: "Portfolio"
      });
    }
    else if(page === "Work")
      console.log(page);
    else if(page === "About"){
      console.log(page);
      this.contentArea = (<About/>);
      this.setState({
        currentPage: "About"
      });
    }
    else if (page === "Contact")
      console.log(page);
    else if(page === "- Fashion & People"){
      this.contentArea = (<Fashion click={this.dialogHandler} app={this}/>);
      this.setState({
        currentPage: "- Fashion & People"
      }); 
    }
    else{
      this.contentArea = (<LandingImage />);
      this.setState({
        currentPage: "Home"
      });
    }
  }

  //Menu button Handler
  leftNavHandler = () => {
    var leftNav = !this.state.LeftNav;

    this.setState({
      LeftNav: leftNav
    });
  }

  leftNavVar=true;
  menuButton=false;
  constructor(props){
    super(props);
    if(window.innerWidth<800){
      this.leftNavVar=false;
      this.menuButton=true;
    }
  }

  render(){
    var buttonMenu = (<button onClick={this.leftNavHandler} className="Menu">Menu</button>);
    if(!this.menuButton)
      buttonMenu=null;
    var LeftNavigation;
    if(this.leftNavVar===false){
      LeftNavigation =(<LeftNavigator style={false} click={this.switchContentAreaHandle}/>);
      this.leftNavVar=true;
      this.setState({LeftNav: false});
    }
    else
      LeftNavigation =(<LeftNavigator style={this.state.LeftNav} click={this.switchContentAreaHandle}/>);

    return (
      <div className="App">
        <div className="AppTitle">
          <Title text = "Prithviraj Chavan" click={this.switchContentAreaHandle}/>
          {buttonMenu}
        </div>
        <div className="AppContainer">
          {LeftNavigation}
          {this.contentArea}
          {this.dialogVariable}
          {/* <p>coming soon!!</p> */}
        </div>
      </div>
    );
  }
}

export default App;
